pub enum HidError {
    HidApiError {
        message: String,
    },
    HidApiErrorEmptyWithCause {
        cause: Box<dyn Error + Send + Sync>,
    },
    HidApiErrorEmpty,
    FromWideCharError {
        wide_char: wchar_t,
    },
    InitializationError,
    OpenHidDeviceError,
    InvalidZeroSizeData,
    IncompleteSendError {
        sent: usize,
        all: usize,
    },
    SetBlockingModeError {
        mode: &'static str,
    },
    OpenHidDeviceWithDeviceInfoError {
        device_info: Box<HidDeviceInfo>,
    },
}
